{% set firewall_blocks = CTX.note.split('\n\n\n') %}
{% set firewall_list = [] %}

{% for block in firewall_blocks %}
    {% set details = block.split('\n') %}
    {% set firewall_dict = {} %}
    {% for detail in details %}
        {% if 'Name:' in detail %}
            {% set key_value = detail.split(': ', 1) %}
            {% set _ = firewall_dict.update({key_value[0]: key_value[1]}) %}
        {% elif 'ID:' in detail %}
            {% set key_value = detail.split(': ', 1) %}
            {% set _ = firewall_dict.update({key_value[0]: key_value[1]}) %}
        {% elif 'Serial:' in detail %}
            {% set key_value = detail.split(': ', 1) %}
            {% set _ = firewall_dict.update({key_value[0]: key_value[1]}) %}
        {% elif 'Current Version:' in detail %}
            {% set key_value = detail.split(': ', 1) %}
            {% set _ = firewall_dict.update({key_value[0]: key_value[1]}) %}
        {% elif 'Updated Version:' in detail %}
            {% set key_value = detail.split(': ', 1) %}
            {% set _ = firewall_dict.update({key_value[0]: key_value[1]}) %}
        {% endif %}
        {% if detail != '' %}
            {% set key_value = ['ticket_id', CTX.ticket_id] %}
            {% set _= firewall_dict.update({key_value[0]: key_value[1]}) %}
            {% set next_key_value = ['date_scheduled', CTX.date] %}
            {% set __= firewall_dict.update({next_key_value[0]: next_key_value[1]}) %}
        {% endif %}
    {% endfor %}
    {% if firewall_dict %}
        {% do firewall_list.append(firewall_dict) %}
    {% endif %}
{% endfor %}

