    {% for exclusion in CTX.exclusion_list|selectattr("sophos_id", "eq", firewall.tenant.id) %}
        {% if exclusion is not none and firewall.serialNumber in exclusion.firewalls %}
            {% set should_include.result = "false" %}
        {% endif %}
    {% endfor %}
    {% if should_include.result == "true" %}
        {% if (firewall.cluster.status|d == "primary" or not(firewall.cluster.status|d)) and firewall.status.suspended == false %}
            {% set firewall_info = {"name": firewall.hostname, "id": firewall.id} %}
            {% do included_firewalls.append(firewall) %}
        {% endif %}
    {% endif %}
{% endfor %}
